            try {
                return externalService.call(data);
            } catch (Exception e) {
                failureCount++;
                lastFailureTime = System.currentTimeMillis();

                // 
                if (failureCount > 10) {
                    throw new CircuitBreakerOpenException("Circuit breaker is open");
                }

                throw new ExternalServiceException("External service call failed", e);
            }
        });
    }
