```go
// server/user_server.go
package server

import (
    "context"
    "log"
    "google.golang.org/grpc"
    pb "github.com/example/proto/user"
)

type UserServer struct {
    pb.UnimplementedUserServiceServer
    users map[int64]*pb.User
}

func NewUserServer() *UserServer {
    return &UserServer{
        users: make(map[int64]*pb.User),
    }
}

// /
func (s *UserServer) GetUser(ctx context.Context, req *pb.GetUserRequest) (*pb.User, error) {
    user, exists := s.users[req.Id]
    if !exists {
        return nil, status.Errorf(codes.NotFound, "User not found: %d", req.Id)
    }
    return user, nil
}

// 
func (s *UserServer) ListUsers(req *pb.ListUsersRequest, stream pb.UserService_ListUsersServer) error {
    page := req.Page
    size := req.Size

    // 
    for _, user := range s.users {
        if err := stream.Send(user); err != nil {
            return err
        }
    }
    return nil
}

// 
func (s *UserServer) CreateUsers(stream pb.UserService_CreateUsersServer) error {
    var users []*pb.User

    for {
        req, err := stream.Recv()
        if err == io.EOF {
            // 
            return stream.SendAndClose(&pb.CreateUsersResponse{
                Count: int32(len(users)),
                Users: users,
            })
        }
        if err != nil {
            return err
        }

        user := &pb.User{
            Id:    int64(len(s.users) + 1),
            Name:  req.Name,
            Email: req.Email,
        }
        s.users[user.Id] = user
        users = append(users, user)
    }
}

// 
func (s *UserServer) ChatUsers(stream pb.UserService_ChatUsersServer) error {
    for {
        msg, err := stream.Recv()
        if err == io.EOF {
            return nil
        }
        if err != nil {
            return err
        }

        // 
        reply := &pb.ChatMessage{
            UserId:  msg.UserId,
            Message: "Echo: " + msg.Message,
        }
        if err := stream.Send(reply); err != nil {
            return err
        }
    }
}

