func StartServer(port string) error {
    lis, err := net.Listen("tcp", port)
    if err != nil {
        return err
    }

    s := grpc.NewServer()
    pb.RegisterUserServiceServer(s, NewUserServer())

    log.Printf("Server listening on %s", port)
    return s.Serve(lis)
}
