func queryDatabase(ctx context.Context, query string) ([]Row, error) {
    rows, err := db.QueryContext(ctx, query)
    if err != nil {
        return nil, err
    }
    defer rows.Close()

    var results []Row
    for rows.Next() {
        select {
        case <-ctx.Done():
            return nil, ctx.Err()
        default:
            var row Row
            if err := rows.Scan(&row); err != nil {
                return nil, err
            }
            results = append(results, row)
        }
    }
    return results, nil
}
