// background.js
class APIInterceptor {
  constructor() {
    this.requests = [];
    this.setupInterception();
  }

  setupInterception() {
    chrome.webRequest.onBeforeRequest.addListener(
      (details) => {
        if (details.url.includes('/api/')) {
          this.requests.push({
            url: details.url,
            method: details.method,
            timestamp: Date.now(),
            requestBody: details.requestBody
          });

          // 
          if (details.requestBody) {
            const formData = details.requestBody.formData;
            if (formData) {
              console.log('Form data:', formData);
            }
          }
        }
      },
      { urls: ['<all_urls>'] },
      ['requestBody']
    );

    chrome.webRequest.onCompleted.addListener(
      (details) => {
        if (details.url.includes('/api/')) {
          const request = this.requests.find(r => 
            r.url === details.url && 
            Math.abs(r.timestamp - Date.now()) < 1000
          );

          if (request) {
            request.response = {
              statusCode: details.statusCode,
              headers: details.responseHeaders,
              timestamp: Date.now()
            };

            // 
            this.saveRequest(request);
          }
        }
      },
      { urls: ['<all_urls>'] },
      ['responseHeaders']
    );
  }

  async saveRequest(request) {
    chrome.storage.local.get(['apiRequests'], (result) => {
      const requests = result.apiRequests || [];
      requests.push(request);

      // 100
      if (requests.length > 100) {
        requests.shift();
      }

      chrome.storage.local.set({ apiRequests: requests });
    });
  }

  async getRequests() {
    return new Promise((resolve) => {
      chrome.storage.local.get(['apiRequests'], (result) => {
        resolve(result.apiRequests || []);
      });
    });
  }
}

const apiInterceptor = new APIInterceptor();
