```javascript
// popup.js
class PopupUI {
  constructor() {
    this.requests = [];
    this.setupEventListeners();
    this.loadRequests();
  }

  setupEventListeners() {
    chrome.runtime.onMessage.addListener((message) => {
      if (message.type === 'NEW_REQUEST') {
        this.addRequest(message.request);
      }
    });
  }

  async loadRequests() {
    chrome.runtime.sendMessage({ type: 'GET_REQUESTS' }, (response) => {
      this.requests = response.requests || [];
      this.render();
    });
  }

  addRequest(request) {
    this.requests.unshift(request);
    if (this.requests.length > 100) {
      this.requests.pop();
    }
    this.render();
  }

  render() {
    const container = document.getElementById('requests');
    container.innerHTML = '';

    this.requests.forEach(request => {
      const item = document.createElement('div');
      item.className = 'request-item';
      item.innerHTML = `
        <span class="method">${request.method}</span>
        <span class="url">${request.url}</span>
        ${request.response ? `<span class="status">${request.response.statusCode}</span>` : ''}
      `;
      item.addEventListener('click', () => this.showDetails(request));
      container.appendChild(item);
    });
  }

  showDetails(request) {
    // 
    console.log('Request details:', request);
  }
}

new PopupUI();
