function transferMoneyWithRetry(fromAccount, toAccount, amount, maxRetries = 3) {
  for (let i = 0; i < maxRetries; i++) {
    try {
      return transferMoney(fromAccount, toAccount, amount);
    } catch (error) {
      if (error.code === 'DEADLOCK' && i < maxRetries - 1) {
        // 
        await sleep(100 * (i + 1)); // 
        continue;
      }
      throw error;
    }
  }
}
