class MultiFactorAuth {
  async setupMFA(userId) {
    // TOTPTime-based One-Time Password
    const secret = speakeasy.generateSecret({
      name: `MyApp (${userId})`,
      issuer: 'MyApp',
    });

    // QR
    const qrCodeUrl = await QRCode.toDataURL(secret.otpauth_url);

