      userId: decoded.userId,
      token: refreshToken,
      expiresAt: { $gt: new Date() },
      revoked: false,
    });

    if (!storedToken) {
      throw new Error('Invalid refresh token');
    }

    // 
    const accessToken = this.generateAccessToken(decoded.userId);

