// AES-256-GCM
const crypto = require('crypto');

class DatabaseEncryption {
  constructor() {
    // 
    this.encryptionKey = Buffer.from(
      process.env.ENCRYPTION_KEY,
      'hex'
    );
    this.algorithm = 'aes-256-gcm';
  }

  encrypt(text) {
    // IV
    const iv = crypto.randomBytes(16);

    // 
    const cipher = crypto.createCipheriv(
      this.algorithm,
      this.encryptionKey,
      iv
    );

    // 
    let encrypted = cipher.update(text, 'utf8', 'hex');
    encrypted += cipher.final('hex');

    // 
    const authTag = cipher.getAuthTag();

    // IV
    return {
      iv: iv.toString('hex'),
      authTag: authTag.toString('hex'),
      encrypted: encrypted,
    };
  }

  decrypt(encryptedData) {
    // 
    const iv = Buffer.from(encryptedData.iv, 'hex');
    const authTag = Buffer.from(encryptedData.authTag, 'hex');
    const encrypted = encryptedData.encrypted;

    // 
    const decipher = crypto.createDecipheriv(
      this.algorithm,
      this.encryptionKey,
      iv
    );

    // 
    decipher.setAuthTag(authTag);

    // 
    let decrypted = decipher.update(encrypted, 'hex', 'utf8');
    decrypted += decipher.final('utf8');

    return decrypted;
  }

  async saveEncryptedData(userId, sensitiveData) {
    // 
    const encrypted = this.encrypt(JSON.stringify(sensitiveData));

    // 
    await db.encryptedData.create({
      userId,
      iv: encrypted.iv,
      authTag: encrypted.authTag,
      encrypted: encrypted.encrypted,
    });
  }

  async getEncryptedData(userId) {
    // 
    const encryptedData = await db.encryptedData.findOne({ userId });

    if (!encryptedData) {
      return null;
    }

    // 
    const decrypted = this.decrypt({
      iv: encryptedData.iv,
      authTag: encryptedData.authTag,
      encrypted: encryptedData.encrypted,
    });

    return JSON.parse(decrypted);
  }
}
