  async correlateEvents(events) {
    // 
    const correlations = [];

    // IP
    const failedLoginsByIP = this.groupBy(events, 'ipAddress')
      .filter(group => 
        group.events.some(e => e.type === 'login_failed')
      );

    for (const group of failedLoginsByIP) {
