    const indexes = await db.indexes.find({ columns });

    return {
      optimal: indexes.length > 0,
      indexes,
    };
  }

  async detectNPlusOne(query) {
    // N+1
    // : 
    const pattern = /SELECT.*FROM users.*SELECT.*FROM orders.*WHERE.*user_id/;

    return {
      detected: pattern.test(query),
      suggestions: [
        'JOIN1',
        'prefetch_related',
      ],
    };
  }
