// OAuth 2.0
class OAuth2Security {
  async validateClient(clientId, redirectUri) {
    // 
    const client = await db.clients.findById(clientId);

    if (!client) {
      throw new Error('Invalid client ID');
    }

    // URI
    if (!client.allowedRedirectUris.includes(redirectUri)) {
      throw new Error('Invalid redirect URI');
    }

    return client;
  }

  async generateState() {
    // CSRFstate
    return crypto.randomBytes(32).toString('hex');
  }

  async generatePKCE() {
    // PKCEProof Key for Code Exchange
    const codeVerifier = crypto.randomBytes(32).toString('base64url');
    const codeChallenge = crypto
      .createHash('sha256')
      .update(codeVerifier)
      .digest('base64url');

    return {
      codeVerifier,
      codeChallenge,
    };
  }

  async validatePKCE(codeVerifier, codeChallenge) {
    // PKCE
    const calculatedChallenge = crypto
      .createHash('sha256')
      .update(codeVerifier)
      .digest('base64url');

    return calculatedChallenge === codeChallenge;
  }
}
