// FIDO2/WebAuthn
class WebAuthnRegistration {
  async register(userId, userName) {
    // 1. 
    const challenge = this.generateChallenge();

    // 2. 
    const publicKeyCredentialCreationOptions = {
      challenge: challenge,
      rp: {
        name: 'MyApp',
        id: 'example.com',
      },
      user: {
        id: this.encodeUserId(userId),
        name: userName,
        displayName: userName,
      },
      pubKeyCredParams: [
        { alg: -7, type: 'public-key' },  // ES256
        { alg: -257, type: 'public-key' }, // RS256
      ],
      authenticatorSelection: {
        authenticatorAttachment: 'platform',  // 
        userVerification: 'required',
        requireResidentKey: false,
      },
      timeout: 60000,
      attestation: 'direct',
    };

    // 3. 
    const credential = await navigator.credentials.create({
      publicKey: publicKeyCredentialCreationOptions,
    });

    // 4. 
    const response = await fetch('/api/webauthn/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        userId,
        credential: {
          id: credential.id,
          rawId: this.arrayBufferToBase64(credential.rawId),
          response: {
            attestationObject: this.arrayBufferToBase64(
              credential.response.attestationObject
            ),
            clientDataJSON: this.arrayBufferToBase64(
              credential.response.clientDataJSON
            ),
          },
          type: credential.type,
        },
        challenge,
      }),
    });

    if (!response.ok) {
      throw new Error('Registration failed');
    }

    return await response.json();
  }

  generateChallenge() {
    // 
    const array = new Uint8Array(32);
    crypto.getRandomValues(array);
    return this.arrayBufferToBase64(array);
  }

  encodeUserId(userId) {
    // IDUint8Array
    return new Uint8Array(Buffer.from(userId, 'utf8'));
  }

  arrayBufferToBase64(buffer) {
    // ArrayBufferBase64
    const bytes = new Uint8Array(buffer);
    let binary = '';
    for (let i = 0; i < bytes.byteLength; i++) {
      binary += String.fromCharCode(bytes[i]);
    }
    return btoa(binary);
  }
}
