      // 
      if (data.type === 'connected') {
        return;
      }

      setNotifications((prev) => [data, ...prev]);

      // 5
      setTimeout(() => {
        setNotifications((prev) =>
          prev.filter((n) => n.id !== data.id)
        );
      }, 5000);
    },
  });

  const handleDismiss = (id: string) => {
    setNotifications((prev) => prev.filter((n) => n.id !== id));
