      .filter(todo => todo.title.toLowerCase().includes(searchQuery.toLowerCase()))
      .sort((a, b) => {
        if (sortOrder === 'asc') return a.title.localeCompare(b.title);
        return b.title.localeCompare(a.title);
      });
  }, [todos, filter, searchQuery, sortOrder]);
}

