// : useEffect

// 1. 
function useUser(userId: string) {
  const [user, setUser] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    let cancelled = false;

    async function fetchUser() {
      setIsLoading(true);
      setError(null);
      try {
        const response = await fetch(`/api/users/${userId}`);
        if (!response.ok) throw new Error('Failed to fetch user');
        const data = await response.json();
        if (!cancelled) {
          setUser(data);
        }
      } catch (err) {
        if (!cancelled) {
          setError(err instanceof Error ? err : new Error('Unknown error'));
        }
      } finally {
        if (!cancelled) {
          setIsLoading(false);
        }
      }
    }

    fetchUser();

    // : 
    return () => {
      cancelled = true;
    };
  }, [userId]); // userId

  return { user, isLoading, error };
}

// 2. 
function useComments(userId: string, enabled: boolean = true) {
  const [comments, setComments] = useState<Comment[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    if (!enabled) return;

    let cancelled = false;

    async function fetchComments() {
      setIsLoading(true);
      setError(null);
      try {
        const response = await fetch(`/api/comments?userId=${userId}`);
        if (!response.ok) throw new Error('Failed to fetch comments');
        const data = await response.json();
        if (!cancelled) {
          setComments(data);
        }
      } catch (err) {
        if (!cancelled) {
          setError(err instanceof Error ? err : new Error('Unknown error'));
        }
      } finally {
        if (!cancelled) {
          setIsLoading(false);
        }
      }
    }

    fetchComments();

    // : 5
    const interval = setInterval(() => {
      if (!cancelled) {
        fetchComments();
      }
    }, 5000);

    // : 
    return () => {
      cancelled = true;
      clearInterval(interval);
    };
  }, [userId, enabled]); // userIdenabled

  return { comments, isLoading, error };
}

// 3. 
function UserProfile({ userId }: { userId: string }) {
  const { user, isLoading: userLoading, error: userError } = useUser(userId);
  const { comments, isLoading: commentsLoading } = useComments(userId);

  if (userLoading) return <LoadingSpinner />;
  if (userError) return <ErrorDisplay error={userError} />;
  if (!user) return <div></div>;

  return (
    <div className="user-profile">
      <UserInfo user={user} />
      <CommentsList
        comments={comments}
        isLoading={commentsLoading}
      />
    </div>
  );
}

