        User cached = localCache.getIfPresent(id);
        if (cached != null) {
            return cached;
        }

        // 2. L2
        String key = "user:" + id;
        cached = redisTemplate.opsForValue().get(key);
        if (cached != null) {
            localCache.put(id, cached);  // L1
            return cached;
        }

        // 3. 
